import React from 'react'
import { useState } from 'react'
import { useEffect } from 'react'

function Firstpagedata({data}) {
  const Gamedata=data.map((Game)=>{
    if(Game.id<5){
      return(
        <div  key={Game.id}>
        <h2 className='popular'></h2>
       <a href={Game.game_url}><div  style={{backgroundImage:`url(${Game.thumbnail})`}} className="img">
        </div></a> 
        </div>
      )
    }
  })

  return (
    < >
    <h2 className='popular'> Popular Games</h2>
     {Gamedata}
    </>
  )
}

export default Firstpagedata
