import React, { useState } from 'react'
import { useEffect } from 'react'
import Menudisplay from './Menudisplay'

function Menudata({Menudata}) {
  const[data,setData]=useState(Menudata)
  const[next,setNext]=useState(true)
  useEffect(()=>{
    if(next){
      return;
    }
    const gamedata=localStorage.getItem("gamestorage");
    if(gamedata){
      setData(JSON.parse(gamedata))
    }
   
    },[])

  useEffect(()=>{
    localStorage.setItem("gamestorage",JSON.stringify(data));
     setData(data)
     setInterval(() => {
      setNext(false)
    }, 5000)},[])
    
  const Games=data.map((game)=>{
    return(<div key={game.id}><Menudisplay data={game}/></div>)
  })
  return (
  <>{Games}</>
  )
}

export default Menudata
