import { useState } from 'react';
import { useEffect } from 'react';
import Navbar from './Navbar/Navbar';
import "./index.css"
import Frontdata from './DataComponent/FRONTIMAGES/Frontdata';
import Firstpagedata from './DataComponent/Firstpagedata'; 
import { BrowserRouter as Router,Routes,Route,Link } from 'react-router-dom';


function App() {
  const[data,setData]=useState([])
 const[background,setBackground]=useState({backgroundColor:"rgb(28,28,28)"})
 const [frontgames,setFrontgames]=useState([])
  const Games=()=>{
    fetch("https://free-to-play-games-database.p.rapidapi.com/api/games", {
      "method": "GET",
      "headers": {
        "x-rapidapi-host": "free-to-play-games-database.p.rapidapi.com",
        "x-rapidapi-key": "65217d46famsh752564dc48c9febp11127ejsnb5c21b7dccdf"
      }
    })
   .then(rsp=>rsp.json())
   .then(data=>{
     setData(data)
     setFrontgames(()=>{ 
     return(
       <Frontdata data={data}/>
     )})

   })
}



useEffect(()=>{
Games()
},[])


  return (
    <div className="App"  style={background}>
      <Router>
           <Navbar/>
              <Routes>
                 <Route path="/"  element={
                   <div>
                   <div className='mainImage'>
                   </div>
                   <div className='FrontOptions'>
                   {frontgames}
                   <h2 className='popular'><div  className='line2'></div><span>Popular</span>   Games<div  className='line'></div></h2>
                   <div className='FrontImage'><Firstpagedata data={data}  no={4} bno={0}/></div>
                   <div className='Lastesg'>
                   <h2 className='popular'><div  className='line2'></div><span>Lastest</span>   Games<div  className='line'></div></h2>
                   <div className='FrontImage'><Firstpagedata data={data}  no={8} bno={4}/></div>
                   </div>
                 </div>
                 </div>} /> 

              </Routes>
     </Router>
    </div>
  );
}

export default App;
