import { useEffect } from 'react';
import Navbar from './Navbar/Navbar';
import Data from './DataComponent/Data'; 
import { BrowserRouter as Router,Routes,Route } from 'react-router-dom';


function App() {
  const[imageChange,setImageChange]=useState(1)
  const[data,setData]=useState([])
  let time;
  const Games=()=>{
    fetch("https://free-to-play-games-database.p.rapidapi.com/api/games", {
      "method": "GET",
      "headers": {
        "x-rapidapi-host": "free-to-play-games-database.p.rapidapi.com",
        "x-rapidapi-key": "65217d46famsh752564dc48c9febp11127ejsnb5c21b7dccdf"
      }
    })
   .then(rsp=>rsp.json())
   .then(data=>{
     setData(data)
   })
}
const timer=()=>{
time=setInterval(() => {
    setImageChange(prevImageChange=>prevImageChange+1)
   }, 2000)};

console.log(imageChange)
 if(imageChange>6){
   setInterval(()=>{
    setImageChange(prevImageChange=>prevImageChange-1)
   },4000)

 }

  const Gamedata=data.map((Game)=>{
    if(Game.id===3){
      return(
        <div key={Game.id}>
          <img src={Game.thumbnail}/>
        </div>
      )
    }
  })
useEffect(()=>{
Games()
timer()
return ()=>clearInterval(time)
},[])
  return (
    <div className="App">
      <Router>
           <Navbar/>
              <Routes>
                 <Route path="/"  element={Gamedata}>

                 </Route>
              </Routes>
     </Router>
    </div>
  );
}

export default App;
