import React from 'react'
import { motion } from 'framer-motion'

function Frontdata({data}) {
 const Framer={
   hidden:{
     opacity:0,
   },
   movement:{
    x:[0,440,200,0],
    opacity:[0,0.3,1,0],
    transition:{delay:3,duration:10,repeat:Infinity,ease:"linear"}
    
   }
  }
  return (
    <div className='frontimages'>
<section>
     <h1>{data[272].title}</h1>
 </section>
    <section>
 <motiondiv 
 variants={Framer} 
 initial="hidden"
 animate="movement"  style={{backgroundImage:`url(${data[57].thumbnail})`}} className='img1'></motiondiv>
 <motion.div style={{backgroundImage:`url(${data[272].thumbnail})`}} className='img2'></motion.div>
 <motion.div style={{backgroundImage:`url(${data[27].thumbnail})`}} className='img3'></motion.div>
 </section>
 <section>
     <h3>{data[272].short_description}</h3>
 </section>
 </div>
  )
}

export default Frontdata
