import { useState } from 'react';
import { useEffect } from 'react';
import Navbar from './Navbar/Navbar';
import "./index.css"
import Data from './DataComponent/Data'; 
import { BrowserRouter as Router,Routes,Route,Link } from 'react-router-dom';


function App() {
  const[data,setData]=useState([])
 const[background,setBackground]=useState({backgroundColor:"rgb(28,28,28)"})
 const [frontgames,setFrontgames]=useState([])
  const Games=()=>{
    fetch("https://free-to-play-games-database.p.rapidapi.com/api/games", {
      "method": "GET",
      "headers": {
        "x-rapidapi-host": "free-to-play-games-database.p.rapidapi.com",
        "x-rapidapi-key": "65217d46famsh752564dc48c9febp11127ejsnb5c21b7dccdf"
      }
    })
   .then(rsp=>rsp.json())
   .then(data=>{
     setData(data)
     setFrontgames(()=>{ 
     return(
      <div className='frontimages'>
     <img src={data[20].thumbnail} className='img1'/>
     <img src={data[21].thumbnail} className='img2'/>
     <img src={data[32].thumbnail} className='img3'/>
     </div>)})

   })
}

  const Gamedata=data.map((Game)=>{
    if(Game.id<10){
      return(
        <div  key={Game.id}>

       <a href={Game.game_url}><div  style={{backgroundImage:`url(${Game.thumbnail})`}} className="img">
        </div></a> 
        </div>
      )
    }
  })

useEffect(()=>{
Games()
},[])


  return (
    <div className="App"  style={background}>
      <Router>
           <Navbar/>
              <Routes>
                 <Route path="/"  element={
                   <div>
                   <div className='mainImage'>

                   </div>
                 <div className='FrontImage'>{Gamedata}</div>
                 </div>} /> 

              </Routes>
     </Router>
    </div>
  );
}

export default App;
