import React, { useState } from 'react'
import { useEffect } from 'react'
import Menudisplay from './Menudisplay'

function Menudata({Menudata}) {
  const[data,setData]=useState(Menudata)
  const rr=()=>
  useEffect(()=>{
    const gamedata=localStorage.getItem("gamestorage");
      setData(JSON.parse(gamedata))
    },[])
  useEffect(()=>{
    localStorage.setItem("gamestorage",JSON.stringify(data))},[data])
  const Games=data.map((game)=>{
    return(<div key={game.id}><button onClick={rr}></button><Menudisplay data={game}/></div>)
  })
  return (
  <>{Games}</>
  )
}

export default Menudata
